import TextEdit from './components/TextEdit.js';

class EditorEasy extends Component {
  constructor(props) {
    super(props)
    this.state = {
      timestamp: 'no timestamp yet',
      text: ''
    };
  }
componentDidMount() {
  api.subscribeToTimer((err, timestamp) => this.setState({
    timestamp
